import { useEffect, useState } from "react"
import Message from "../services/ChatGPT"
import "./BoxChat.css"

function Loading({ message, messages }) {
	const [res, setRes] = useState({
		status: false,
		text: "",
	})
	useEffect(() => {
		const fetchData = async () => {
			try {
				const res = messages.map((info) => info.message).join("/n")
				const text = await Message(res)
				setRes(() => {
					return {
						status: true,
						text,
					}
				})
			} catch {}
		}
		fetchData()
	}, [])

	return !res.status ? <span className="loader"></span> : res.text
}

export default Loading
