import { useRef } from "react"

function InputChat({ onSubmit }) {
	const form = useRef()

	return (
		<div className="chat-input">
			<form id="form" ref={form}>
				<input
					type="text"
					placeholder="Type your message here..."
					className="chat-input-field"
				/>
			</form>
		</div>
	)
}

export default InputChat
